import React from "react";

class SearchBar extends React.Component {
    state = {value: "Search Somthing..."}
    onChange = (e) => {
        this.setState({value: e.target.value})
    }
    onSubmit = (e) => {
        e.preventDefault()
        this.props.getInput(this.state.value)
    }

    render(){
        return(
            <div className="d-flex justify-content-center mt-4">
                <form onSubmit={this.onSubmit}>
                    <input onChange={this.onChange} value={this.state.value}  type="text" className="border border-3 rounded-2 p-1"></input>
                </form>
                <button onClick={this.onSubmit} className="btn btn-outline-success mx-1 p-1 px-2">Search</button>
            </div>
        )
    }
}

export default SearchBar;
