import React from "react";
import axios from "axios";
import SearchBar from "./SearchBar";
import VideoList from "./VideoList";

class App extends React.Component {
    state = {Response: ""}

    getInput = input => {
        axios.get("https://www.googleapis.com/youtube/v3/search", {
            params: {
                q: input,
                key: "AIzaSyBT8q6KDWyVJKAnyzlWIXTKgWfBVQSbIFw",
                part: "snippet", //snippet have a data such as thumbnails & MetaData
                maxResults: 5,
            }
        }).then((Response) => {
            this.setState({Response: Response})
        })
    }



    render(){
        return(
                <div className="container-fluid">
                    <SearchBar getInput = {this.getInput} />
                    <div className="d-flex flex-row justify-content-center border border-4 col-12 mt-4">
                        <div className="bg-success col-7"></div>
                        <div className="col-4 d-flex justify-content-center bg-danger">
                            <VideoList Response = {this.state.Response} />
                        </div>
                    </div>
                </div>
            )
    }
}

export default App;
